import React from "react";
import ReactDOM from "react-dom/client";
import "./style.css";
import FWPIC from "./myReqs/20230822_123712.jpg"
import IFPIC from "./myReqs/20231124_172144.jpg"
import TAPIC from "./myReqs/IMG_20230820_131024.jpg"
import NMPIC from "./myReqs/20230812_124906.jpg"
import ITPIC from "./myReqs/20231106_230439.jpg"

const BOOK1 = {
  pic:{FWPIC},
  title:"FOURTH WING",
  aut:"REBECCA YARROS",
};
const BOOK2 = {
  pic:{IFPIC},
  title:"IRON FLAME",
  aut:"REBECCA YARROS",
};
const BOOK3 = {
  pic:{TAPIC},
  title:"THE ALCHEMIST",
  aut:"PAULO COELHO",
};
const BOOK4 = {
  pic:{NMPIC},
  title:"NO MATTER WHAT I WILL ALWAYS LOVE YOU",
  aut:"ROHIT DAWESAR",
};
const BOOK5 = {
  pic:{ITPIC},
  title:"IN THE PRESENCE OF ABSENCE",
  aut:"M",
};

const BookList = () => {
  return (
    <section className="FlexADD">
      <Book />
      <Book />
      <Book />
      <Book />
      <Book />
      <Book />
    </section>
  );
};

const Book = (props) => {
  return (
    <article className="CardClass">
      <Image img={props.IMG} />
      <Title title={props.TITLE}/>
      <Author author={props.AUTHOR}/>
    </article>
  );
};

const Image = (props) => {
  return (
    <img src={props.FWPIC} alt="FOURTH WING by Rebecca Yarros" width={300} height={300} />
  );
};
const Title = () => {
  return <h2>FOURTH WING</h2>;
};
const Author = () => {
  return <h1 style={{color:"#aaf",textShadow:"0 0 2px #aaf"}}>REBECCA YARROS</h1>;
};

const root = ReactDOM.createRoot(document.getElementById("root"));

root.render(<BookList />);
